return (
    <Router>
      <div className="min-h-screen bg-gray-50">
        <Toaster 
          position="top-center"
          richColors
          closeButton
          duration={3000}
          toastOptions={{
            style: {
              fontSize: '14px',
            },
          }}
        />
        <Routes>
            {/* 公开路由 */}
            <Route path="/login" element={<Login />} />

            {/* 受保护的路由 */}
            <Route path="/" element={
              <ProtectedRoute>
                <Layout>
                  <Home />
                </Layout>
              </ProtectedRoute>
            } />

            <Route path="/purchase-entry" element={
              <ProtectedRoute>
                <Layout>
                  <PurchaseEntry />
                </Layout>
              </ProtectedRoute>
            } />

            <Route path="/purchase-list" element={
              <ProtectedRoute>
                <Layout>
                  <PurchaseList />
                </Layout>
              </ProtectedRoute>
            } />



            <Route path="/product-entry" element={
              <ProtectedRoute>
                <Layout>
                  <ProductEntry />
                </Layout>
              </ProtectedRoute>
            } />



            <Route path="/sales-list" element={
              <ProtectedRoute>
                <Layout>
                  <SalesList />
                </Layout>
              </ProtectedRoute>
            } />

            <Route path="/inventory-list" element={
              <ProtectedRoute>
                <Layout>
                  <InventoryList />
                </Layout>
              </ProtectedRoute>
            } />

            <Route path="/financial" element={
              <ProtectedRoute>
                <Layout>
                  <Financial />
                </Layout>
              </ProtectedRoute>
            } />

            <Route path="/supplier-management" element={
              <ProtectedRoute required_role="BOSS">
                <Layout>
                  <SupplierManagement />
                </Layout>
              </ProtectedRoute>
            } />

            <Route path="/customer-management" element={
              <ProtectedRoute>
                <Layout>
                  <CustomerManagement />
                </Layout>
              </ProtectedRoute>
            } />

            <Route path="/user-management" element={
              <ProtectedRoute required_role="BOSS">
                <Layout>
                  <UserManagement />
                </Layout>
              </ProtectedRoute>
            } />

            <Route path="/settings" element={
              <ProtectedRoute>
                <Layout>
                  <Settings />
                </Layout>
              </ProtectedRoute>
            } />

            {/* 默认重定向 */}
            <Route path="*" element={<Navigate to="/" replace />} />
          </Routes>
        </div>
      </Router>

